import React from "react";
import { useLocation, useParams, useHistory } from "react-router";

//? Without Param Hooks

// const User = ( {match} ) => {

//     return (
//         <>
//             <h1>I am {match.params.name}</h1>
//         </>
//     );
// }

//? With Param Hooks

const User = () => {
  const { fName, lName } = useParams();
  const location = useLocation();
  const history = useHistory();
  console.log(history);

  return (
    <React.Fragment>
      <h1>
        I am {fName} {lName}
      </h1>
      <br />
      <p>My Current Location is {location.pathname}</p>

      <br />
      <br />
      {location.pathname === `/user/roshan/sharma` ? (
        <button onClick={ () => alert("Welcome Sir!!") } >Click Me</button>
      ) : null}
    </React.Fragment>
  );
};

export default User;
