import React, { useState } from 'react';
import './index.css';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';


const CreateNote = (props) => {

  const [expand, setExpand] = useState(false);

  const [note, setNote] = useState({
    title: '',
    content: ''
  })

  const inputEvent = (event) => {

    const {value, name} = event.target;
    setNote( (preVal) => {
      return {
        ...preVal,
        [name] : value
      }
    })
  }

  const addEvent = () => {
    props.passNote(note)
    setNote({
      title: '',
      content: ''
    })
  }

  const expandIt = () => {
    setExpand(true)
  }

  const shrinkIt = () => {
    setExpand(false);
  }

  return(
    <>
        <div className="main_note" onDoubleClick={shrinkIt}>
            <form>

              {
                expand ? <input type="text" value={note.title} name="title" onChange={inputEvent} placeholder="Title"/> : null
              }
                <textarea row="" cols="" onClick={expandIt} value={note.content} name="content" onChange={inputEvent} placeholder="Write A Note"></textarea>

              {
                
                expand ? <Button className="MuiButton-root" onClick={addEvent}>
                  <AddIcon className="plus_sign" />
                </Button> : null
              }
            </form>
        </div>
    </>

    
  );
}


export default CreateNote;
