import React, { useState } from 'react';
import './index.css';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';


const CreateNote = (props) => {

  const [note, setNote] = useState({
    title: '',
    content: ''
  })

  const inputEvent = (event) => {

    const {value, name} = event.target;
    setNote( (preVal) => {
      return {
        ...preVal,
        [name] : value
      }
    })
  }

  const addEvent = () => {
    props.passNote(note)
    setNote({
      title: '',
      content: ''
    })
  }

  return(
    <>
        <div className="main_note">
            <form>
                <input type="text" value={note.title} name="title" onChange={inputEvent} placeholder="Title"/>
                <textarea row="" cols="" value={note.content} name="content" onChange={inputEvent} placeholder="Write A Note"></textarea>
                <Button className="MuiButton-root" onClick={addEvent}>
                  <AddIcon className="plus_sign" />
                </Button>
            </form>
        </div>
    </>

    
  );
}


export default CreateNote;
